INSERT INTO `oc_information_description` (`information_id`, `language_id`, `title`, `description`) VALUES
(8, 2, 'Agende um Serviço', '&lt;p&gt;A WeCare Auto&amp;nbsp;tem à disposição mais este canal de comunicação para seus clientes não perderem um minuto do seu tempo para serem atendidos. Você pode a qualquer momento solicitar o acionamento de um&amp;nbsp;ou mais serviços do seu pacote para seu carro. Você também pode contratar serviços extras, de acordo com sua necessidade, com condições especiais que só cliente WeCare Auto tem. Fique tranquilo que no primeiro horário do dia útil seguinte a sua solicitação, um de nossos analistas cuidará de seu pedido pra você.&lt;/p&gt;\r\n\r\n&lt;h3&gt;Atendemos veículos de passeio e/ou utilitários, Nacionais e Importados, de até 3 toneladas (Peso Bruto Total).&lt;/h3&gt;\r\n\r\n&lt;p style=&quot;margin-left: 360px;&quot;&gt;&lt;img alt=&quot;&quot; src=&quot;http://www.wecareauto.com.br/image/data/televendas.png&quot; style=&quot;width: 220px; height: 81px;&quot; /&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;iframe frameborder=&quot;0&quot; height=&quot;450&quot; name=&quot;agende&quot; src=&quot;http://www.wecareauto.com.br/extra-cont/agende-um-servico/index.html&quot; width=&quot;100%&quot;&gt;&lt;/iframe&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n'),
(13, 2, 'Como funciona', '&lt;h2&gt;Entenda como funcionam os nossos serviços.&lt;/h2&gt;\r\n\r\n&lt;p&gt;&lt;a href=&quot;http://www.wecareauto.com.br/compare-os-nossos-pacotes-e-escolha-o-ideal-para-voce&quot; style=&quot;line-height: 1.6em;&quot;&gt;&lt;img alt=&quot;&quot; src=&quot;http://www.wecareauto.com.br/image/data/BANNER EMESON2.png&quot; style=&quot;width: 970px; height: 433px;&quot; /&gt;&lt;/a&gt;&lt;/p&gt;\r\n\r\n&lt;h3&gt;&lt;strong&gt;Se você precisa de ajuda para controlar a Agenda do Carro, não conhece mecânica automotiva e não tem tempo para levar seu carro às manutenções, nós da WeCare Auto temos a solução para você.&lt;/strong&gt;&lt;/h3&gt;\r\n\r\n&lt;h3&gt;&lt;strong&gt;Ao contratar a WeCare Auto&amp;nbsp;você fica despreocupado, pois, conta com :&lt;/strong&gt;&lt;/h3&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;&lt;img alt=&quot;&quot; src=&quot;http://www.wecareauto.com.br/image/data/lista 2.png&quot; style=&quot;width: 980px; height: 205px;&quot; /&gt;&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;div&gt;\r\n&lt;div style=&quot;font-size: 13px;&quot;&gt;&lt;a href=&quot;http://www.wecareauto.com.br/veja-tudo-que-podemos-fazer-por-voce&quot; style=&quot;line-height: 1.6em;&quot;&gt;&lt;img alt=&quot;&quot; src=&quot;http://www.wecareauto.com.br/image/data/Nossos serviços 3.png&quot; style=&quot;width: 320px; height: 68px;&quot; /&gt;&lt;/a&gt;&lt;span style=&quot;line-height: 1.6em;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;a href=&quot;http://www.wecareauto.com.br/compare-os-nossos-pacotes-e-escolha-o-ideal-para-voce&quot; style=&quot;line-height: 1.6em;&quot;&gt;&lt;img alt=&quot;&quot; src=&quot;http://www.wecareauto.com.br/image/data/para pessoas 3.png&quot; style=&quot;width: 320px; height: 68px;&quot; /&gt;&lt;/a&gt;&lt;span style=&quot;line-height: 1.6em;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;a href=&quot;http://www.wecareauto.com.br/compare-os-nossos-pacotes-e-escolha-o-ideal-para-a-sua-empresa&quot; style=&quot;line-height: 1.6em;&quot;&gt;&lt;img alt=&quot;&quot; src=&quot;http://www.wecareauto.com.br/image/data/para empresas 3.png&quot; style=&quot;width: 320px; height: 68px;&quot; /&gt;&lt;/a&gt;&lt;span style=&quot;line-height: 1.6em;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/div&gt;\r\n\r\n&lt;div style=&quot;font-size: 13px;&quot;&gt;&amp;nbsp;&lt;/div&gt;\r\n\r\n&lt;div style=&quot;font-size: 13px;&quot;&gt;\r\n&lt;h3&gt;&lt;span style=&quot;line-height: 1.2em; font-size: 13px;&quot;&gt;Confira aqui como em poucos passos você contrata um pacote WeCare Auto e, rapidamente, já tem acesso a todas as facilidades e benefícios de nossos serviços.&lt;/span&gt;&lt;/h3&gt;\r\n&lt;/div&gt;\r\n&lt;/div&gt;\r\n\r\n&lt;div&gt;\r\n&lt;div&gt;&lt;img alt=&quot;&quot; src=&quot;http://www.wecareauto.com.br/image/data/wecare-como-funciona.jpg&quot; style=&quot;opacity: 0.9; font-size: 13.333333969116211px; width: 976px; height: 640px;&quot; /&gt;&lt;span style=&quot;line-height: 1.6em;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;&lt;/span&gt;&lt;/div&gt;\r\n\r\n&lt;div&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;&lt;img alt=&quot;&quot; src=&quot;http://www.wecareauto.com.br/image/data/selo promoção.png&quot; style=&quot;opacity: 0.9; font-size: 13px; width: 700px; height: 129px;&quot; /&gt;&lt;span style=&quot;line-height: 1.6em;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp;&amp;nbsp;​&lt;/span&gt;&lt;/div&gt;\r\n&lt;/div&gt;\r\n\r\n&lt;div&gt;\r\n&lt;h3&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;&lt;img alt=&quot;&quot; src=&quot;http://www.wecareauto.com.br/image/data/selo.jpg&quot; style=&quot;opacity: 0.9; font-size: 13px; width: 700px; height: 173px;&quot; /&gt;&lt;/h3&gt;\r\n\r\n&lt;h3 style=&quot;margin-left: 240px;&quot;&gt;&lt;img alt=&quot;&quot; src=&quot;http://www.wecareauto.com.br/image/data/televendas.png&quot; style=&quot;font-size: 13px; line-height: 1.6em; width: 200px; height: 74px;&quot; /&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;&lt;a href=&quot;http://www.wecareauto.com.br/ultimas-noticias&quot;&gt;&lt;img alt=&quot;&quot; src=&quot;http://www.wecareauto.com.br/image/data/BLOG VF.png&quot; style=&quot;width: 229px; height: 74px;&quot; /&gt;&lt;/a&gt;&lt;/h3&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;h3&gt;&lt;strong style=&quot;font-size: 13px; line-height: 1.6em;&quot;&gt;Atendemos na Região da Grande São Paulo, veículos de passeio e/ou utilitários, Nacionais e Importados, de até 3 toneladas &lt;/strong&gt;&lt;span style=&quot;font-size: 13px; line-height: 1.6em;&quot;&gt;(Peso Bruto Total).&lt;/span&gt;&lt;/h3&gt;\r\n\r\n&lt;h1&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;&amp;nbsp;&lt;/h1&gt;\r\n\r\n&lt;h3&gt;Está esperando o que? Solicite agora o seu Pacote e obtenha:&lt;/h3&gt;\r\n\r\n&lt;h1&gt;Mais cuidado para seu carro.&lt;br /&gt;\r\nMais tempo pra você.&lt;/h1&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;img alt=&quot;&quot; src=&quot;http://www.wecareauto.com.br/image/data/linha.jpg&quot; style=&quot;width: 976px; height: 17px;&quot; /&gt;&lt;/p&gt;\r\n&lt;/div&gt;\r\n'),
(15, 2, 'Sócios', '&lt;p&gt;Formada por executivos do mercado segurador com ampla experiência no mercado de serviços.&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;u&gt;Emerson Bernardes, 25 anos de experiência&amp;nbsp; no mercado de serviços&lt;/u&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Administrador de Empresas com MBA em Gestão Financeira pela Fipecafi – USP.&lt;/p&gt;\r\n\r\n&lt;p&gt;Foi diretor para a América Latina da ACE Seguradora, Head de Personal Lines na Unibanco AIG Seguros, Membro do Conselho da Seguradora Líder dos Consórcios DPVAT, Consultor na Zurich Seguros e Gerente executivo de Auto na Allianz Seguros.&lt;/p&gt;\r\n\r\n&lt;p&gt;Atualmente é o Sócio Administrador da WeCare Auto.&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;u&gt;Cesar Saad, mais de 35 anos de experiência&amp;nbsp; no mercado de serviços&lt;/u&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Engenheiro Civil com MBA pela COPPEAD RJ e Fundação Dom Cabral/INSEAD 1995. Certificado pelo Chartered Insurance Institute (CII) of London. Diretor dos Sindicatos das Seguradoras no Rio de Janeiro, Diretor da FENASEG, trabalhou 30 anos na Unibanco AIG Seguros (1975/2004), onde durante 13 anos foi Diretor Executivo. Exerceu a função de Diretor Técnico do IRB-Brasil Resseguros S.A., foi membro do Conselho Técnico do IRB-Instituto de Resseguros do Brasil e dos Conselhos de Administração do CESVI Brasil,&amp;nbsp;SBCE- Seguradora Brasileira de Crédito à Exportação, do IRB-Brasil Resseguros S.A., da TEMPO Participações S.A., da CHARTIS Brasil.&lt;/p&gt;\r\n\r\n&lt;p&gt;Atualmente é membro do Conselho do Grupo SUHAI e Consultor em Estratégia Empresarial na área de Seguros e Resseguros.&lt;/p&gt;\r\n'),
(14, 2, 'Meu pacote ideal', '&lt;p&gt;&lt;strong&gt;Na WeCare Auto você não perde tempo nem para escolher o seu pacote!&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;br /&gt;\r\nAo responder o questionário, tenha a garantia de que nós já nos preocupamos em indicar a melhor opção de pacote para seu estilo de vida ou necessidade da sua empresa.&lt;br /&gt;\r\n&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;iframe frameborder=&quot;0&quot; height=&quot;620&quot; name=&quot;meupacote&quot; src=&quot;http://www.wecareauto.com.br/extra-cont/meu-pacote-ideal/index.php&quot; width=&quot;100%&quot;&gt;&lt;/iframe&gt;&lt;/p&gt;\r\n'),
(12, 2, 'Área de atuação', '&lt;p&gt;Estamos situados no Itaim Bibi e disponibilizamos nossos serviços especializados WeCare Auto aos clientes que tenham veículos que circulem e pernoitem na cidade de São Paulo ou em uma das cidades indicadas no mapa abaixo.&lt;br /&gt;\r\nNossa rede referenciada está estrategicamente localizada para garantir agilidade, conforto e tranquilidade em cada atendimento.&lt;/p&gt;\r\n\r\n&lt;h3&gt;Atendemos veículos de passeio e/ou utilitários, Nacionais e Importados, de até 3 toneladas (Peso Bruto Total).&lt;br /&gt;\r\n&amp;nbsp;&lt;/h3&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;Veja abaixo nosso mapa de atuação:&lt;/strong&gt;&lt;br /&gt;\r\n&lt;br /&gt;\r\n&lt;br /&gt;\r\n&lt;span style=&quot;font-size: 13px;&quot;&gt;&lt;img alt=&quot;&quot; src=&quot;http://www.wecareauto.com.br/image/data/mapa-area-atuacao.jpg&quot; style=&quot;width: 978px; height: 560px;&quot; /&gt;&lt;/span&gt;&lt;br style=&quot;font-size: 13px;&quot; /&gt;\r\n&amp;nbsp;&lt;/p&gt;\r\n'),
(9, 2, 'Seja um Revendedor WeCare Auto', '&lt;p&gt;Oferecemos soluções completas de produtos e serviços automotivos para você inovar seu portfólio e surpreender seus clientes com soluções inovadoras.&lt;/p&gt;\r\n\r\n&lt;p&gt;Entre em contato conosco e conheça melhor a WeCare Auto.&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;a href=&quot;mailto:sejarevendedor@wecareauto.com.br&quot;&gt;sejarevendedor@wecareauto.com.br&lt;/a&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n'),
(10, 2, 'Trabalhe na WeCare Auto', '&lt;p&gt;Se você é apaixonado por carro, gosta de desafios e adora surpreender um cliente com soluções inovadoras e muita atitude, então a WeCare Auto tem a oportunidade perfeita para a sua carreira.&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;br /&gt;\r\nEnvie seu currículo para: &lt;a href=&quot;mailto:trabalheconosco@wecareauto.com.br&quot;&gt;trabalheconosco@wecareauto.com.br&lt;/a&gt;&lt;/p&gt;\r\n'),
(16, 2, 'Compare os nossos pacotes e escolha o ideal para você!', '&lt;p&gt;&lt;img alt=&quot;&quot; src=&quot;http://www.wecareauto.com.br/image/data/selo promoção.png&quot; style=&quot;width: 970px; height: 178px;&quot; /&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;img alt=&quot;&quot; src=&quot;http://www.wecareauto.com.br/image/data/serviços comparativo.jpg&quot; style=&quot;line-height: 1.6em; opacity: 0.9; width: 139px; height: 108px;&quot; /&gt;&lt;a href=&quot;http://www.wecareauto.com.br/para-voce/wecare-flex&quot;&gt;&lt;img alt=&quot;&quot; src=&quot;http://www.wecareauto.com.br/image/data/caixa flex 2.png&quot; style=&quot;font-size: 13px; line-height: 1.6em; opacity: 0.9; width: 119px; height: 108px;&quot; /&gt;&lt;/a&gt;&lt;a href=&quot;http://www.wecareauto.com.br/para-voce/wecare-flex-mais&quot;&gt;&lt;img alt=&quot;&quot; src=&quot;http://www.wecareauto.com.br/image/data/caixa flex mais.png&quot; style=&quot;width: 117px; height: 116px;&quot; /&gt;&lt;/a&gt;&lt;a href=&quot;http://www.wecareauto.com.br/para-voce/wecare-day-by-day&quot; style=&quot;font-size: 13px; line-height: 1.6em;&quot;&gt;&lt;img alt=&quot;&quot; src=&quot;http://www.wecareauto.com.br/image/data/daybyday comparativo.jpg&quot; style=&quot;line-height: 1.6em; opacity: 0.9; width: 113px; height: 108px;&quot; /&gt;&lt;/a&gt;&lt;span style=&quot;font-size: 13px; line-height: 1.6em;&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;a href=&quot;http://www.wecareauto.com.br/para-voce/wecare-plus&quot; style=&quot;font-size: 13px; line-height: 1.6em;&quot;&gt;&lt;img alt=&quot;&quot; src=&quot;http://www.wecareauto.com.br/image/data/plus comparativo.jpg&quot; style=&quot;line-height: 1.6em; opacity: 0.9; width: 117px; height: 108px;&quot; /&gt;&lt;/a&gt;&lt;span style=&quot;font-size: 13px; line-height: 1.6em;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;a href=&quot;http://www.wecareauto.com.br/para-voce/wecare-executivo&quot; style=&quot;font-size: 13px; line-height: 1.6em;&quot;&gt;&lt;img alt=&quot;&quot;