	$message .= '<p><a href="' . HTTP_CATALOG . '" title="' . HTTP_CATALOG . '"><img src="'. HTTP_IMAGE . $this->config->get('config_logo') .'" alt="" style="border:0;" /></a></p>';
}
$message .=      html_entity_decode($this->request->post['message'], ENT_QUOTES, 'UTF-8');
$message .= '  </body>' . "\n";
